import React,{useState} from "react";
import CreateArea from "../components/CreateArea.jsx";
import Header from "../components/Header.jsx";
import Footer from "../components/Footer.jsx";
function Register(){
    
    const [fullname,setFullName] = useState({
        fName:"",
        lName:"",
    });
    const [email,setEmail] = useState("");
    const [password,setPassword] = useState("");
    var [ele,setEle] = useState(0);
    function handleChangeName(event,inputName){
        const newVal = event.target.value;
        if (inputName === "fname") {
        setFullName({ fName: newVal, lName: fullname.lName });
        } else {
        setFullName({ fName: fullname.fName, lName: newVal });
        }
        console.log(inputName);
    }
    function handleForm(event) {
        event.preventDefault();

        // Send a POST request to the server

        ///should do something over here 
        
    }
    function getNext(){
        if (ele === 1){
            if (email.search("@gmail.com") === -1)
                setEle(ele);
        }
        else
            setEle(++ele);   
    }
    function handleChangeEmail(event){
        const val = event.target.value;
        setEmail(val);
    }
    function handleChangePassword(event){
        const val = event.target.value;
        setPassword(val);
    }
    const customStyle={
        paddingBottom:"40px"
    }


    return (
        
        <div class="container">
            <Header visibility="1"/>
            <div class="register-container">
                
                <h1 id="homepage-subhead" style={customStyle}>Welcome to SplitEqual</h1>
                <h1>Hello {fullname.fName} {fullname.lName}</h1>
                
                <form onSubmit={handleForm}>
                    {ele === 0 &&  (<>          
                        <CreateArea name="fname" placer="First Name" changeDetector={handleChangeName}/>
                        <CreateArea name="lname" placer="Last Name" changeDetector={handleChangeName} />
                    </>
                    )}
                    {ele === 1 && (<>
                        <CreateArea name="email" placer="Email-ID" changeDetector={handleChangeEmail} />
                    </>)
                    }
                    {ele === 2 &&(<>
                        <CreateArea name="password" placer="password" changeDetector={handleChangePassword} />
                    </>)
                    }
                    <button class="submit" type="submit" onClick={getNext}>Submit</button>
                </form>
            </div>
            <Footer />
        </div>
    )
}
export default Register;
