function App(){
    const [fullname,setFullName] = useState({
        fName:"",
        lName:"",
    });
    function handleChange(event,inputName){
        const newVal = event.target.value;
        if (inputName === "fname") {
        setFullName({ fName: newVal, lName: fullname.lName });
        } else {
        setFullName({ fName: fullname.fName, lName: newVal });
        }
        console.log(inputName);
    }
    function handleForm(event) {
        event.preventDefault();

        // Send a POST request to the server

        ///should do something over here 
        
    }




    return (
        <div class="register-container">
            <Header />
            <h1 id="homepage-subhead">Welcome to SplitEqual</h1>
            <h1>Hello {fullname.fName} {fullname.lName}</h1>
            <form onSubmit={handleForm}>
                <CreateArea name="fname" placer="First Name" changeDetector={handleChange}/>
                <CreateArea name="lname" placer="Last Name" changeDetector={handleChange} />
                <button class="submit" type="submit">Submit</button>
            </form>
            <Footer />
        </div>
    )
}
export default App;
